import { configureStore, createSlice } from '@reduxjs/toolkit';

const initialState = {count: 0, showNumCount: true};

const countSlice = createSlice({
    name: 'counter',
    initialState: initialState,
    reducers: {
      increment: (state) => { state.count += 1; },
      decrement: (state) => { state.count -= 1; },
      increase: (state, action)=> { state.count += action.amount},
      toggleChange: (state) => {!state.showNumCount},
    //   aheee: (state) => {  console.log(state); },
    },
  });



//   countSlice.actions.increment();

 export const countActions = countSlice.actions;

const store = configureStore({
    counter : //key is match a name of slice
     countSlice.reducer
    });

export default store;
